          };

          console.log("New form data after OCR update:", newFormData);
          console.log(
            "Documents in new form data after OCR:",
            newFormData.documents
          );
          console.log(
            "Documents count in new form data after OCR:",
            newFormData.documents.length
          );

          return newFormData;
        });

        if (
          extracted.fullName ||
          extracted.dob ||
          extracted.idType ||
          extracted.idNumber ||
          extracted.address
        ) {
          addAlert(
