          );
        }
      } catch (err) {
        console.error("OCR extraction error:", err);
        addAlert(
          `Failed to extract details from document: ${err.message}. Please fill manually.`,
          "error"
        );
      } finally {
        setOcrProcessing(false);
      }
    } else {
      setOcrProcessing(false);
