            <h3 className="form-section-title">
              Document Upload & Basic Information
            </h3>
            <div
              className="document-upload"
              onDrop={async (e) => {
                e.preventDefault();
                if (!ocrProcessing) {
                  await handleFileUpload(e.dataTransfer.files);
                }
              }}
              onDragOver={(e) => e.preventDefault()}
              onClick={() => {
                if (!ocrProcessing) {
                  document.getElementById("file-input").click();
                }
              }}
              style={{
                opacity: ocrProcessing ? 0.6 : 1,
                cursor: ocrProcessing ? "not-allowed" : "pointer",
              }}
            >
              {ocrProcessing ? (
                <>
                  <div className="upload-spinner">
                    <div className="spinner"></div>
                  </div>
                  <div className="upload-text">
                    <p>Processing document with OCR...</p>
                    <p>
                      Please wait while we extract information from your
                      document
                    </p>
                  </div>
                </>
              ) : (
                <>
                  <div className="upload-icon">📄</div>
                  <div className="upload-text">
                    <p>Drag and drop your documents here, or click to browse</p>
                    <p>Supported formats: PDF, JPG, PNG (Max 10MB each)</p>
                  </div>
                </>
              )}
              <input
                id="file-input"
                type="file"
                multiple
                accept=".pdf,.jpg,.jpeg,.png"
                style={{ display: "none" }}
                onChange={async (e) => {
                  if (!ocrProcessing) {
                    await handleFileUpload(e.target.files);
                  }
                }}
              />
            </div>

            {formData.documents.length > 0 && (
              <div className="file-list">
                <h4>Uploaded Documents:</h4>
                {formData.documents.map((doc) => (
                  <div key={doc.id} className="file-item">
                    <div>
                      <div className="file-name">{doc.name}</div>
                      <div className="file-size">
                        {formatFileSize(doc.size)}
                      </div>
                    </div>
                    <button
                      className="remove-file"
                      onClick={() => removeFile(doc.id)}
                      title="Remove file"
                    >
                      ✕
                    </button>
                  </div>
                ))}
              </div>
            )}

            {/* OCR Processing Indicator */}
            {ocrProcessing && (
              <div className="ocr-processing-simple">
                <span className="simple-spinner">⏳</span>
                <span>Processing document... {formatTimer(processingTimer)}</span>
              </div>
            )}

            {/* Document-related fields */}
            <h4>Document Information</h4>
            <div className="grid grid-2 document-info-grid" style={{display: 'flex', alignItems: 'flex-end', gap: '1.2rem'}}>
              <div className="input-group" style={{flex: '1', display: 'flex', flexDirection: 'column'}}>
                <label className="input-label">Full Name *</label>
                <input
                  type="text"
                  value={formData.customer.fullName}
                  onChange={(e) =>
                    handleInputChange("customer", "fullName", e.target.value)
                  }
                  disabled={ocrProcessing}
                  style={{height: '44px', padding: '0.75rem 1rem', boxSizing: 'border-box'}}
                />
              </div>
              <div className="input-group" style={{flex: '1', display: 'flex', flexDirection: 'column'}}>
                <label className="input-label">Date of Birth *</label>
                <input
                  type="date"
                  value={formData.customer.dob}
                  onChange={(e) =>
                    handleInputChange("customer", "dob", e.target.value)
                  }
                  disabled={ocrProcessing}
                  style={{height: '44px', padding: '0.75rem 1rem', boxSizing: 'border-box'}}
                />
              </div>
            </div>
            <div className="grid equal-cols">
              <div className="input-group">
                <label className="input-label">Complete Address *</label>
                <textarea
                  value={formData.address.line1}
                  onChange={(e) =>
                    handleInputChange("address", "line1", e.target.value)
                  }
                  rows="3"
                  disabled={ocrProcessing}
                  style={{
                    width: "100%",
                    resize: "vertical",
                    fontFamily: "inherit",
                    fontSize: "inherit",
                    padding: "8px 12px",
                    border: "1px solid #ddd",
                    borderRadius: "4px",
                  }}
                />
              </div>
            </div>
            <div className="grid equal-cols" style={{display: 'flex', alignItems: 'flex-end', gap: '1.2rem'}}>
              <div className="input-group" style={{flex: '1', display: 'flex', flexDirection: 'column'}}>
                <label className="input-label">ID Type *</label>
                <select
                  value={formData.ids.idType}
                  onChange={(e) =>
                    handleInputChange("ids", "idType", e.target.value)
                  }
                  required
                  disabled={ocrProcessing}
                  style={{height: '44px', padding: '0.75rem 1rem', boxSizing: 'border-box'}}
                >
                  <option value="">Select ID Type</option>
                  <option value="aadhaar">Aadhaar</option>
                  <option value="pan">PAN Card</option>
                  <option value="voter">Voter ID</option>
                  <option value="passport">Passport</option>
                </select>
              </div>
              <div className="input-group" style={{flex: '1', display: 'flex', flexDirection: 'column'}}>
                <label className="input-label">{formData.ids.idType ? `${formData.ids.idType.toUpperCase()} Number` : 'ID Number'} *</label>
                <input
                  type="text"
                  value={formData.ids.idNumber}
                  onChange={(e) =>
                    handleInputChange("ids", "idNumber", e.target.value)
                  }
                  required
                  disabled={ocrProcessing}
                  style={{height: '44px', padding: '0.75rem 1rem', boxSizing: 'border-box'}}
                />
              </div>
            </div>
          </div>
        );

      case 2:
        // Step 2: Contact Info, Address Details, and Product Info
        return (
          <div className="form-section">
            <h3 className="form-section-title">
              Contact & Product Information
            </h3>

            <h4>Contact Information</h4>
            <div className="grid grid-2" style={{display: 'flex', alignItems: 'flex-end', gap: '1.2rem'}}>
              <div className="input-group" style={{flex: '1', display: 'flex', flexDirection: 'column'}}>
                <label className="input-label">Mobile Number *</label>
                <input
                  type="tel"
                  value={formData.customer.mobile}
                  onChange={(e) =>
                    handleInputChange("customer", "mobile", e.target.value)
                  }
                  style={{height: '44px', padding: '0.75rem 1rem', boxSizing: 'border-box'}}
                />
              </div>
              <div className="input-group" style={{flex: '1', display: 'flex', flexDirection: 'column'}}>
                <label className="input-label">Email Address *</label>
                <input
                  type="email"
                  value={formData.customer.email}
                  onChange={(e) =>
                    handleInputChange("customer", "email", e.target.value)
                  }
                  style={{height: '44px', padding: '0.75rem 1rem', boxSizing: 'border-box'}}
                />
              </div>
            </div>

            <h4>Personal Details</h4>
            <div className="grid grid-2" style={{display: 'flex', alignItems: 'flex-end', gap: '1.2rem'}}>
              <div className="input-group" style={{flex: '1', display: 'flex', flexDirection: 'column'}}>
                <label className="input-label">Occupation</label>
                <input
                  type="text"
                  value={formData.customer.occupation}
                  onChange={(e) =>
                    handleInputChange("customer", "occupation", e.target.value)
                  }
                  style={{height: '44px', padding: '0.75rem 1rem', boxSizing: 'border-box'}}
                />
              </div>
              <div className="input-group" style={{flex: '1', display: 'flex', flexDirection: 'column'}}>
                <label className="input-label">Income Band</label>
                <select
                  value={formData.customer.income_band}
                  onChange={(e) =>
                    handleInputChange("customer", "income_band", e.target.value)
                  }
                  style={{height: '44px', padding: '0.75rem 1rem', boxSizing: 'border-box'}}
                >
                  <option value="">Select Income Band</option>
                  <option value="0-2L">0-2L</option>
                  <option value="2L-5L">2L-5L</option>
                  <option value="5L-10L">5L-10L</option>
                  <option value="10L+">10L+</option>
                  <option value="Not provided">Not provided</option>
                </select>
              </div>
            </div>
            <div className="grid grid-1">
              <div className="checkbox-container">
                <label>
                  <input
                    type="checkbox"
                    checked={formData.customer.pep}
                    onChange={(e) =>
                      handleInputChange("customer", "pep", e.target.checked)
                    }
                  />
                  <span className="checkbox-label">
                    I am a Politically Exposed Person (PEP)
                  </span>
                </label>
              </div>
            </div>

            <h4>Address Details</h4>
            <div className="grid grid-2" style={{display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1.2rem', alignItems: 'end'}}>
              <div className="input-group" style={{display: 'flex', flexDirection: 'column'}}>
                <label className="input-label">City *</label>
                <input
                  type="text"
                  value={formData.address.city}
                  onChange={(e) =>
                    handleInputChange("address", "city", e.target.value)
                  }
                  style={{height: '44px', padding: '0.75rem 1rem', boxSizing: 'border-box'}}
                />
              </div>
              <div className="input-group" style={{display: 'flex', flexDirection: 'column'}}>
                <label className="input-label">State *</label>
                <input
                  type="text"
                  value={formData.address.state}
                  onChange={(e) =>
                    handleInputChange("address", "state", e.target.value)
                  }
                  style={{height: '44px', padding: '0.75rem 1rem', boxSizing: 'border-box'}}
                />
              </div>
              <div className="input-group" style={{display: 'flex', flexDirection: 'column'}}>
                <label className="input-label">PIN Code *</label>
                <input
                  type="text"
                  value={formData.address.pin}
                  onChange={(e) =>
                    handleInputChange("address", "pin", e.target.value)
                  }
                  style={{height: '44px', padding: '0.75rem 1rem', boxSizing: 'border-box'}}
                />
              </div>
              <div className="input-group" style={{display: 'flex', flexDirection: 'column'}}>
                <label className="input-label">Country *</label>
                <input
                  type="text"
                  value={formData.address.country}
                  onChange={(e) =>
                    handleInputChange("address", "country", e.target.value)
                  }
                  style={{height: '44px', padding: '0.75rem 1rem', boxSizing: 'border-box'}}
                />
              </div>
            </div>

            <h4>Product Details</h4>
            <div className="grid grid-2" style={{display: 'flex', alignItems: 'flex-end', gap: '1.2rem'}}>
              <div className="input-group" style={{flex: '1', display: 'flex', flexDirection: 'column'}}>
                <label className="input-label">Account Type *</label>
                <select
                  value={formData.product.desired_account}
                  onChange={(e) =>
                    handleInputChange(
                      "product",
                      "desired_account",
                      e.target.value
                    )
                  }
                  style={{height: '44px', padding: '0.75rem 1rem', boxSizing: 'border-box'}}
                >
                  <option value="">Select Account Type</option>
                  <option value="savings">Savings</option>
                  <option value="current">Current</option>
                </select>
              </div>
              <div className="input-group" style={{flex: '1', display: 'flex', flexDirection: 'column'}}>
                <label className="input-label">Expected Monthly Balance *</label>
                <select
                  value={formData.product.expected_mab_range}
                  onChange={(e) =>
                    handleInputChange(
                      "product",
                      "expected_mab_range",
                      e.target.value
                    )
                  }
                  style={{height: '44px', padding: '0.75rem 1rem', boxSizing: 'border-box'}}
                >
                  <option value="">Select Balance Range</option>
                  <option value="0-1L">0-1L</option>
                  <option value="1L-5L">1L-5L</option>
                  <option value="5L+">5L+</option>
                </select>
              </div>
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="container">
      {!showOnboarding ? (
        <div className="start-screen">
          <h1>Welcome to Customer Portal</h1>
          <div
            style={{
              display: "flex",
              gap: "1rem",
              justifyContent: "center",
              flexWrap: "wrap",
            }}
          >
            <button
              className="btn btn-primary"
              onClick={() => setShowOnboarding(true)}
            >
              Start Onboarding
            </button>
          </div>
        </div>
      ) : isSubmitted ? (
        <>
          <h1 className="page-title">Customer Onboarding</h1>
          {renderSuccessScreen()}
        </>
      ) : (
        <>
          <h1 className="page-title">Customer Onboarding</h1>

          {alerts.map((alert) => (
            <div key={alert.id} className={`alert alert-${alert.type}`}>
              {alert.message}
            </div>
          ))}

          <div className="workflow-container">
            {/* Steps Indicator */}
            <div className="step-indicator">
              {steps.map((step, index) => (
                <div key={step.id} className="step">
                  <div
                    className={`step-circle ${
                      index + 1 < currentStep
                        ? "completed"
                        : index + 1 === currentStep
                        ? "active"
                        : "pending"
                    }`}
                  >
                    {index + 1 < currentStep ? "✓" : step.id}
                  </div>
                  <div
                    className={`step-label ${
                      index + 1 < currentStep
                        ? "completed"
                        : index + 1 === currentStep
                        ? "active"
                        : ""
                    }`}
                  >
                    {step.title}
                  </div>
                </div>
              ))}
            </div>

            <div className="progress-bar">
              <div
                className="progress-fill"
                style={{
                  width: `${((currentStep - 1) / (steps.length - 1)) * 100}%`,
                }}
              />
            </div>

            {renderStepContent()}

            <div className="step-navigation">
              {currentStep > 1 && (
                <button
                  className="btn btn-secondary"
                  onClick={handlePreviousStep}
                  disabled={loading}
                >
                  Previous
                </button>
              )}
              {currentStep === 1 && (
                <button
                  className="btn btn-primary"
                  onClick={handleNextStep}
                  disabled={loading}
                >
                  {loading ? "Processing..." : "Next"}
                </button>
              )}
              {currentStep === 2 && (
                <button
                  className="btn btn-success"
                  onClick={handleSubmitFromStep2}
                  disabled={loading}
                >
                  {loading ? "Submitting Application..." : "Submit Application"}
                </button>
              )}
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default CustomerOnboarding;
